import { createRouter, createWebHistory } from 'vue-router';
import EmailConfirmation from '../components/EmailConfirmation.vue';
import Home from '../components/Home.vue';
import CreatePasswordForm from '../components/CreatePasswordForm.vue'; // Importa el componente de creación de contraseña

const routes = [
  { path: '/', name: 'Home', component: Home }, // Página principal
  { path: '/confirm-email', name: 'ConfirmEmail', component: EmailConfirmation }, // Confirmación de correo
  { path: '/create-password', name: 'CreatePassword', component: CreatePasswordForm }, // Creación de contraseña
  { path: '*', redirect: '/' }, // Redirige rutas no encontradas a la página principal
];

const router = createRouter({
  history: createWebHistory(), // Usa el historial del navegador
  routes,
});

export default router;
